function redCurtain()
{var colors = [0xFC0808,0xBF0D0D ];
 	var alphas = [0.2, 1];
	 var ratios = [10,225];
 	var matr = $.createMatrix();
 	matr.createGradientBox(10, 10,0,0,0);

	var cbx=-10;
	var moveKey=7;
	var moveKey2=3;	
	for(var i=1;i<=7;i++)
	{

	if(i<=4)
	{	
		moveKey2=moveKey2-0.5;

	}
	else
	{

		moveKey2=moveKey2+0.5;
	}	

	var g=$.createShape({x:0,y:0,lifeTime:11,motionGroup:[  
   {y:{fromValue:342,toValue:342,lifeTime:moveKey}},
   {y:{fromValue:342,toValue:0,lifeTime:moveKey2},alpha:{fromValue:0.8,toValue:0,lifeTime:moveKey2}}]});
    g.graphics.beginGradientFill("linear", colors, alphas, ratios, matr, "reflect","linearRGB",0);
	g.graphics.drawRoundRect(cbx,-342,80,342,5,5);		
	cbx=cbx+80;

	}
}
