  function textPxjy(t,x,y1,wz,lt)
{
var x1=270-wz/1.8*t.length;    /*置中，指定则换x*/
var key=(lt-1-0.2)/(2*t.length);
var gf1=$.createGlowFilter(0xF7D4D4/*光晕颜色*/,0.8/*Alpha*/,0/*水平模糊量*/,0/*垂直模糊量*/,28/*印记或跨页的强度*/,1/*应用滤镜的次数*/,false/*是否为内侧发光*/,false/*是否具有挖空效果*/);	
 for(var i=0;i<t.length;i++)
 {var w=$.createComment(t[i],{x:x1-wz/1.8,y:y1,lifeTime:lt,alpha:1,motionGroup:[ 
 { alpha: {fromValue:0,toValue:0,lifeTime:key*(i+1)}}, 
   {x:{fromValue:x1-wz/1.8,toValue:x1+i*(wz+15)/1.8,lifeTime:0.2},alpha: {fromValue:0,toValue:1,lifeTime:0.2}},
   { alpha: {fromValue:1,toValue:1,lifeTime:1}},
   {x:{fromValue:x1+i*(wz+15)/1.8,toValue:x1+i*wz/1.8-4,lifeTime:key*(i+1)},alpha: {fromValue:1,toValue:0,lifeTime:key*(i+1)}}    
 ]});
w.fontsize=wz;
w.font="宋体";
w.filters=[gf1];
 }

}
