function numberOut(t,x,y,i)
{

	var key=0;
var gf1=$.createGlowFilter(0xF70505/*光晕颜色*/,0.7/*Alpha*/,12/*水平模糊量*/,12/*垂直模糊量*/,2/*印记或跨页的强度*/,10/*应用滤镜的次数*/,true/*是否为内侧发光*/,false/*是否具有挖空效果*/);	
	interval(function(){key=key+1;var n=$.createComment(key,{x:x,y:y,lifeTime:t/i,color:0xFFFFFF,alpha:1});
           n.fontsize=100;n.filters=[gf1];	},t*1000/i,i);

timer(function(){w=$.createComment("で",{x:x-25,y:y+5,lifeTime:t,alpha:1});w.font="宋体";w.fontsize=100;w.filters=[gf1];},t*1000+t*1000/i);





}

function textPx(t,x1,y1,wz,lt)
{
var key1=lt/0.04;
var key2=360/key1;
var gf1=$.createGlowFilter(0xFFFFFF/*光晕颜色*/,0.7/*Alpha*/,12/*水平模糊量*/,12/*垂直模糊量*/,2/*印记或跨页的强度*/,10/*应用滤镜的次数*/,true/*是否为内侧发光*/,false/*是否具有挖空效果*/);

 var i=0;
 	interval(function(){ var w=$.createComment(t,{x:x1,y:y1,lifeTime:3,alpha:1});
w.font="宋体";
w.filters=[gf1];i++;w.rotationZ=i*key2;},40,key1);

w.fontsize=wz;
}

