 function arrow(t,x1,y1,x2,lt)
{
var gf1=$.createGlowFilter(0xffffff/*光晕颜色*/,0.8/*Alpha*/,0/*水平模糊量*/,0/*垂直模糊量*/,5/*印记或跨页的强度*/,10/*应用滤镜的次数*/,true/*是否为内侧发光*/,false/*是否具有挖空效果*/);		
var c = $.createCanvas({x:x1,y:y1,lifeTime:lt,motionGroup:[{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 0.5}},{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 0.5}},{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 1}},{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 1}},{rotationX:{ fromValue: 0, toVaclue: 360, lifeTime: 1}},{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 1}},{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 1}},{rotationX:{ fromValue: 0, toValue: 360, lifeTime: 1}}]});
var w=$.createComment(t,{x:0,y:-10,lifeTime:lt,alpha:1,parent:c,motionGroup:[
   {x:{fromValue:0,toValue:x2,lifeTime:0.5,easing:exponential}}   ]});
w.fontsize=18;
w.color=0x000000;
w.font="宋体";
w.filters=[gf1];
}
