function openLight()
{



	var x=key*50;		
	for(var i=0;i<8;i++)
	{var c=$.createCanvas({x:0,y:384,lifeTime:7,motionGroup:[
	{x:{fromValue:0,toValue:641,lifeTime:4.2},y:{fromValue:384,toValue:-71,lifeTime:4.2}	}]	});
	var g = $.createShape({x:0,y:0,lifeTime:4.2,parent:c});
 	g.graphics.beginFill(0xFFFFFF,0.2);	
	 g.graphics.drawCircle(0,0,100);}


 }








