function whiteScreen()
{
	var g = $.createShape({x:0,y:42,lifeTime:80});
        g.graphics.beginFill(0xFFFFFF,1);	
	g.graphics.drawRect(0,0,542,300);

}
function blackScreen()
{
	var g = $.createShape({x:0,y:41,lifeTime:5});
        g.graphics.beginFill(0x000000,1);	
	g.graphics.drawRect(0,0,541,302);

}
function openDoor()
{

	var g1 = $.createShape({x:-1,y:42,lifeTime:4.5,motionGroup:[{rotationY:{fromValue:0,toValue:100,lifeTime:4.5}}]});
        g1.graphics.beginFill(0x000000,1);	
	    g1.graphics.drawRect(0,0,271,300);
	var g2= $.createShape({x:544,y:42,lifeTime:4.5,motionGroup:[{rotationY:{fromValue:0,toValue:-100,lifeTime:4.5}}]});
		g2.graphics.beginFill(0x000000,1);
        g2.graphics.drawRect(0,0,-274,300);



}


