 var w=$.createComment(getw,{x:x1,y:y1+1.1*wz*(i-1),lifeTime:lt,alpha:1,motionGroup:[{alpha:{ fromValue: 0, toValue: 1, lifeTime:1.5}},{x:{fromValue:x1,toValue:15,lifeTime:1.5},rotationZ:{fromValue:0, toValue:-25, lifeTime:1.5}},{alpha:{ fromValue: 1, toValue: 0, lifeTime:1}}]});
