 function textM(t,x1,y1,x2,y2,wz,gfc,lt,rz)
{	

var gf1=$.createGlowFilter(gfc/*光晕颜色*/,1/*Alpha*/,2/*水平模糊量*/,2/*垂直模糊量*/,5/*印记或跨页的强度*/,5/*应用滤镜的次数*/,true/*是否为内侧发光*/,false/*是否具有挖空效果*/);
