function helpYourself(t)
{
var gfc=["0x990af3","0x0c62af","0xf70b10","0xFF1C60","0xf5c60a","0xf1f40b","0x39f55c"];


var i=0;

interval(function(){
i++;
x1=Utils.rand(0,541);
