	var w=$.createComment(getw,{x:x1+i*0.9*wz,y:gety,lifeTime:lt,alpha:1,parent:c,motionGroup:[ 
   {y:{fromValue:gety,toValue:gety2,lifeTime:0.5,easing:elastic},alpha: {fromValue:0,toValue:1,lifeTime:0.5}},
    {y:{fromValue:gety2,toValue:gety,lifeTime:0.5,easing:elastic}},
   {y:{fromValue:gety,toValue:gety2,lifeTime:0.5,easing:elastic}},
  {y:{fromValue:gety2,toValue:0,lifeTime:0.5,easing:elastic}}

 ]});
