function noThink(t,key)
{
var gf1=$.createGlowFilter(0x000000/*光晕颜色*/,1/*Alpha*/,0/*水平模糊量*/,0/*垂直模糊量*/,0/*印记或跨页的强度*/,0/*应用滤镜的次数*/,true/*是否为内侧发光*/,false/*是否具有挖空效果*/);
var c3=$.createCanvas({x:250,y:180,lifeTime:2.36,motionGroup:[
{rotationX:{fromValue:0,toValue:360,lifeTime:0.8}}]});

for(var i=1;i<=t.length;i++)
{

var getw=t.substring(i-1,i);
getx=-Math.sin(30*i*Math.PI/180)*key;
gety=Math.cos(30*i*Math.PI/180)*key;
var w=$.createComment(getw,{x:getx,y:gety,lifeTime:2.36,alpha:1,parent:c3});
w.font="宋体";	
 w.filters=[gf1];
w.color=0xF80707;
w.color=0x000000;
w.fontsize=30;


}


}
