	var gf1=$.createGlowFilter(glc,0.8,1,1,1,1,true,false);			
	var c=$.createCanvas({x:x1,y:y1,lifeTime:lt,motion:{rotationZ:{fromValue:0,toValue:360*lt,lifeTime:lt}}});  /*x:270,y:197中点坐标*/
	var i=0;
	 interval(function(){ i++;	var g = $.createShape({x:0,y:0,lifeTime:0.035,parent:c});
 	g.graphics.lineStyle(1/*粗细*/, cs/*颜色*/, 0.5/*透明度*/, false/*完整像素true或false*/, "vertical"/*缩放normal,none等*/,"none"/*端点类型none、round 和 square*/, "miter"/*拐角bevel、miter 和 round*/, 10/*切断尖角的数字*/);	
	 g.graphics.drawCircle(0,0,10*i);
        g.filters=[gf1];},40,25);			
	var j=0;
	 interval(function(){ 
if(j<24)
{
lt=0.01;
}
if(j==24)
{
lt=getlt-0.01*24;
}
j++;	
