/*表*/
function showClock(cx,cy,H,M)              /*cx,cy钟表位置参数*/
{ var c = $.createCanvas({x:cx,y:cy,lifeTime:7});
	var R=100;
	var gf1=$.createGlowFilter(0xFFD700/*光晕颜色*/,0.8/*Alpha*/,2/*水平模糊量*/,2/*垂直模糊量*/,25/*印记或跨页的强度*/,10/*应用滤镜的次数*/,true/*是否为内侧发光

*/,false/*是否具有挖空效果*/);
    var clockNbm=["Ⅻ","Ⅰ","Ⅱ","Ⅲ","Ⅳ","Ⅴ","Ⅵ","Ⅶ","Ⅷ","Ⅸ","Ⅹ","Ⅺ"];
	for(var n=0;n<12;n++)
	{
	var getx=R*Math.sin(n*30*Math.PI/180)-15;
	var gety=-R*Math.cos(n*30*Math.PI/180)-10;
	var ct=$.createComment(clockNbm[n],{x:getx,y:gety,lifeTime:5,parent:c});
	ct.filters=[gf1];
	ct.fontsize=20;
}
R=R-45;
var gethx=R*Math.sin((M*0.5+H*30)*Math.PI/180);
var gethy=-R*Math.cos((M*0.5+H*30)*Math.PI/180);
R=R+20;
var getmx=R*Math.sin(M*6*Math.PI/180);
var getmy=-R*Math.cos(M*6*Math.PI/180);
var d= $.createShape({lifeTime:5,x:0,y:0,parent:c});
d.graphics.lineStyle(10, 0x030303, 1, false, "vertical","round", "round", 10);
d.graphics.lineTo(gethx,gethy);
d.graphics.moveTo(0,0);
d.graphics.lineStyle(5, 0x030303, 1, false, "vertical","none", "round", 10);
d.graphics.lineTo(getmx,getmy);

}


