function kuankuan(t,x1,y1,wz,lt,cln)
{
var cl=["0x990af3","0x0c62af","0xf70b10","0xFF1C60","0xf5c60a","0xf1f40b","0x39f55c","0x13d3be","0x000000","0xF7CA00"];
for(var i=1;i<=t.length;i++){
var getw=t.substring(i-1,i);
var getx1=x1+(i-1)*wz*0.9;
var w2x1=Utils.rand(-50,590);
var w2y1=Utils.rand(50,320);
var w2=$.createComment("█",{x:w2x1,y:w2y1,lifeTime:lt,alpha:1,motionGroup:[{alpha:{fromValue:0,toValue:1,lifeTime:0.1},y:{fromValue:w2y1,toValue:y1,lifeTime:0.4},x:{fromValue:w2x1,toValue:getx1,lifeTime:0.2}}]});
	w2.color=cl[cln];
	w2.fontsize=wz;
	w2.font="宋体";
	w2.filters=null;	
var w=$.createComment(getw,{x:getx1,y:y1,lifeTime:lt,alpha:1});	
	w.color=0xffffff;
	w.fontsize=wz;
	w.font="宋体";
	w.filters=null;
}
}

