function daoying(t,x1,y1,wz,gfn,lt,r1,r2)
{
var x1=(541-wz*0.9*t.length)/2;
var cl=["0x990af3","0x0c62af","0xf70b10","0xFF1C60","0xf5c60a","0xf1f40b","0x39f55c","0x13d3be","0x000000","0xF7CA00"];
var gf1=$.createGlowFilter(cl[gfn],1,2,2,2,6,true,false);
for(i=1;i<=t.length;i++)
{

	var tw= t.substring(i-1,i);
	getx1=x1+(i-1)*wz*0.9;
	var w=$.createComment(tw,{x:getx1,y:y1,lifeTime:lt,alpha:1,motionGroup:[{alpha:{fromValue:0,toValue:1,lifeTime:0.3}},{alpha:{fromValue:1,toValue:0,lifeTime:0.2,startDelay:(lt-0.5)*1000}}]});



/*倒影*/
	var w2=$.createComment(tw,{x:getx1,y:y1+2.5*wz,lifeTime:lt,motionGroup:[{alpha:{fromValue:0,toValue:0.3,lifeTime:0.3}},{alpha:{fromValue:0.3,toValue:0,lifeTime:0.2,startDelay:(lt-0.5)*1000}}]});
if(i>=r1&&i<=r2)
{	w.color=w2.color=cl[2];w.filters=null;w2.filters=null;

}
else
{	w.color=0xffffff;
	w.filters=[gf1];
	w2.color=0xffffff;
	w2.filters=[gf1];

}	w.fontsize=wz;	
	w.font="宋体";
	w2.rotationX=180;	
	w2.fontsize=wz;
	w2.alpha=0.3;
	w2.font="宋体";



}
}
