function redCurtain()
{var colors = [0xFC0808,0xBF0D0D ];
 	var alphas = [0.2, 1];
	 var ratios = [10,225];
 	var matr = $.createMatrix();
 	matr.createGradientBox(10, 10,0,0,0); 	
	var cbx=-10;
for(var i=1;i<=7;i++)
	{	
	var g=$.createShape({x:0,y:0,lifeTime:8,motionGroup:[  
   {y:{fromValue:-342,toValue:0,lifeTime:2},alpha:{fromValue:0,toValue:0.8,lifeTime:2}}]});
    g.graphics.beginGradientFill("linear", colors, alphas, ratios, matr, "reflect","linearRGB",0);
	g.graphics.drawRoundRect(cbx,0,80,342,5,5);		
	cbx=cbx+80;

	}
}
