function kuanshu(t,x1,y1,wz,lt,cln)
{
var cl=["0x990af3","0x0c62af","0xf70b10","0xFF1C60","0xf5c60a","0xf1f40b","0x39f55c","0x13d3be","0x000000","0xF7CA00"];
for(var i=1;i<=t.length;i++){
var getw=t.substring(i-1,i);
var gety1=y1+(i-1)*wz*0.9;
var w2x1=Utils.rand(-500,600);
var w2y1=Utils.rand(50,320);
var w2=$.createComment("█",{x:w2x1,y:w2y1,lifeTime:lt,alpha:1,motionGroup:[{alpha:{fromValue:0,toValue:1,lifeTime:0.1},y:{fromValue:w2y1,toValue:gety1,lifeTime:0.1},x:{fromValue:w2x1,toValue:x1,lifeTime:0.1}}]});
	w2.color=cl[8];
	w2.fontsize=wz;
	w2.font="宋体";
	w2.filters=null;	
var w=$.createComment(getw,{x:x1,y:gety1,lifeTime:lt,alpha:1});	
	w.color=cl[cln];
	w.fontsize=wz;
	w.font="宋体";
	w.filters=null;
}
}

timer(function(){
funlogo(541/2,192,2.3);

timer(function(){kuanshu(t1,50,50,27,0.5,5);
kuanshu(t11,85,90,20,0.5,5);},500);

timer(function(){
kuanshu(t2,450,50,27,0.5,4);
kuanshu(t22,485,90,20,0.5,4);},1000);

timer(function(){
kuanshu(t3,50,50,27,0.5,2);
kuanshu(t33,85,90,20,0.5,2);},1500);

timer(function(){
kuanshu(t4,450,40,26,0.5,0);
kuanshu(t44,485,90,20,0.5,0);},2000);},750);

