timer(function(){
t1="考えないで馬鹿になろう？";
t11="考えないで馬鹿になろう？\n拋開一切不要多想了嘛？";
	var wz=25;
	var cl=["0x990af3","0x0c62af","0xf70b10","0xFF1C60","0xf5c60a","0xf1f40b","0x39f55c","0x13d3be","0x000000","0xF7CA00"];
	var arrow_height=wz*4;
	var width=wz*4.5;
	var height=t1.length*wz;
	var w=width/2;	
	var c= $.createCanvas({x:0,y:195,lifeTime:2,motionGroup:[{x:{ fromValue: 0, toValue:50+arrow_height+height,lifeTime:0.2}},{rotationX:{fromValue:0, toValue:360,lifeTime:0.2,startDelay:1600},x:{ fromValue:50+arrow_height+height, toValue:560+arrow_height+height,lifeTime:0.2,startDelay:1600}}]});
	var g= $.createShape({lifeTime:2,x:0,y:0,parent:c});
	g.graphics.beginFill(cl[2],0.8);
 	g.graphics.lineTo(-arrow_height,-w);
 	g.graphics.lineTo(-arrow_height,w);
 	g.graphics.lineTo(0,0);
 	g.graphics.drawRect(-arrow_height,w/2,-height,-w);
var w=$.createComment(t11,{x:-(arrow_height+height),y:-wz,lifeTime:2,parent:c});	
w.fontsize=wz;
w.filters=null;
w.color=0xffffff;
w.font="宋体";},40);

