t1="(少年人形)「アカリガ」";
t11="(人偶少年)「在那燈火」";
t2="(少女人形)「ツクマデ...」";
t22="(人偶少女)「點亮之前...」";
function fBack(t,y1,wz,lt,cln,r1,r2)
{
var gf1=$.createGlowFilter(0x000000/*光晕颜色*/,1/*Alpha*/,2/*水平模糊量*/,2/*垂直模糊量*/,8/*印记或跨页的强度*/,5/*应用滤镜的次数*/,true/*是否为内侧发光*/,false/*是否具有挖空效果*/);
var cl=["0x990af3","0x0c62af","0xf70b10","0xFF1C60","0xf5c60a","0xf1f40b","0x39f55c","0x13d3be","0x000000","0xffffff"];
var getwz=wz*10;
var getx1=(541-getwz*t.length*0.9)/2;

var getx2=(541-wz*t.length*0.9)/2;
gety2=y1+wz*11;
for(var i=1;i<=t.length;i++)
{


var getw=t.substring(i-1,i);
var w=$.createComment(getw,{x:getx2,y:gety2,lifeTime:lt,alpha:1,motionGroup:[{fontsize:{fromValue:wz,toValue:getwz,lifeTime:lt*0.6},y:{fromValue:gety2,toValue:y1,lifeTime:lt*0.6},x:{fromValue:getx2,toValue:getx1,lifeTime:lt*0.6}}]});
if(i>=r1&&i<=r2)
{
w.color=cl[2];
}	
else
{w.color=cl[cln];} 
	w.fontsize=wz;
	w.font="宋体";
	w.filters=[gf1];
getx1=getx1+getwz*0.9;
getx2=getx2+wz*0.9;
}
}



timer(function(){
fBack(t1,100,3,2.1,4,26,27);
fBack(t11,140,2,2.1,4,26,27);},50);

timer(function(){
fBack(t2,200,3,1.1,5,26,27);
fBack(t22,240,2,1.1,5,26,27);},1000);
